        this.authservice = authservice;
        this.cliente = {
            uid: '',
            email: '',
            passwordd: '',
            nombrerefe: '',
            usuarioubi: ''
        };
        this.uid = '';
    }
    async ngOnInit() {
        const uid = await this.authservice.getAuthUid();
        console.log(uid);
        setTimeout(() => {
            this.captchaCreator();
