import { USUARIOS } from './Misusuarios';
import * as i0 from "@angular/core";
import * as i1 from "@angular/fire/compat/firestore";
import * as i2 from "@angular/fire/compat/auth";
export class SharedService {
    constructor(firestore, afauth) {
        this.firestore = firestore;
        this.afauth = afauth;
        this.user = USUARIOS;
        this.getAuthUid();
    }
    getuser() {
        return this.user;
    }
    settam(tam, posicion) {
        this.user[posicion].tam = tam;
    }
    //CRUD
    addArticle(article) {
        return this.firestore.collection('articles').add(article);
    }
    getArticles() {
        return this.firestore.collection('articles', ref => ref.orderBy('name', 'asc')).snapshotChanges();
    }
    deleteArticle(id) {
        return this.firestore.collection('articles').doc(id).delete();
    }
    getArticle(id) {
        return this.firestore.collection('articles').doc(id).snapshotChanges();
    }
    updateArticle(id, data) {
        return this.firestore.collection('articles').doc(id).update(data);
    }
    //USUARIOS
    createDoc(data, path, id) {
        const collection = this.firestore.collection(path);
        return collection.doc(id).set(data);
    }
    getDoc(path, id) {
        const collection = this.firestore.collection(path);
        return collection.doc(id).valueChanges;
    }
    deleteDoc(path, id) {
        const collection = this.firestore.collection(path);
        return collection.doc(id).delete;
    }
    updateDoc(data, path, id) {
        const collection = this.firestore.collection(path);
        return collection.doc(id).update(data);
    }
    getUid() {
        return this.firestore.createId();
    }
    getCollection(path) {
        const collection = this.firestore.collection(path);
        return collection.valueChanges;
    }
    //AUTENTICACION
    register(email, password) {
        return this.afauth.createUserWithEmailAndPassword(email, password);
    }
    login(email, password) {
        return this.afauth.signInWithEmailAndPassword(email, password);
    }
    logout() {
        return this.afauth.signOut();
    }
    async getAuthUid() {
        const user = await this.afauth.currentUser;
        if (user === null) {
            return null;
        }
        else {
            return user.uid;
        }
    }
    stateAuth() {
        return this.afauth.authState;
    }
}
